import React, {useState} from "react";
import ExpenseForm from "./ExpenseForm";



const ExpenseFormStates = () => {
    const formState = {cancelState: false, submitState:false, basicState: true};
    const [changedformState, setFormState] = useState();

    const formStateHandler = (event) => {
        console.log(formState);
        formState.basicState = false;
        console.log(formState);
        setFormState(formState);
    };

    const basicFormState = <button onClick={formStateHandler}>Add New Expense</button>


    return (
            <div>
                {basicFormState}
            </div>
    );
};

export default ExpenseFormStates;
