import React, { useState } from "react";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import ExpensesList from "./ExpensesList";
import ExpensesChart from "./ExpensesChart";

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState(
    new Date().getMonth().toString()
  );

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };
  const filteredExpenses = props.data.filter((expenseItem) => {
    console.log("expenseItem", expenseItem.date.getMonth());
    return expenseItem.date.getFullYear().toString() === filteredYear;
    //expenseItem.date.toString().includes(filteredYear)
  });

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          options={props.data.map((expense) =>
            expense.date.getFullYear().toString()
          )}
          onFilterChange={filterChangeHandler}
        />
        <ExpensesChart expenses={filteredExpenses} />
        <ExpensesList data={filteredExpenses} />
      </Card>
    </div>
  );
}
export default Expenses;
