import React, { useState } from "react";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import ExpensesList from "./ExpensesList";
import ExpensesChart from "./ExpensesChart";

const months = [
  "Jan",
  "Feb",
  "Mar",
  "Apr",
  "May",
  "Jun",
  "Jul",
  "Aug",
  "Sep",
  "Oct",
  "Nov",
  "Dec",
];

function Expenses(props) {
  const [filteredMonth, setFilteredMonth] = useState(new Date().getMonth());

  const filterChangeHandler = (selectedMonth) => {
    setFilteredMonth(selectedMonth);
  };
  const filteredExpenses = props.data.filter((expenseItem) => {
    return parseInt(expenseItem.date.getMonth()) === parseInt(filteredMonth);
  });

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredMonth}
          options={props.data.map((expense) =>
            expense.date.getMonth().toString()
          )}
          onFilterChange={filterChangeHandler}
        />
        <ExpensesChart expenses={filteredExpenses} />
        <ExpensesList data={filteredExpenses} />
      </Card>
    </div>
  );
}
export default Expenses;
